mapper.update:
update t_caete
set C_Name = #{cName,jdbcType=VARCHAR},
  C_Count = #{cCount,jdbcType=INTEGER},
  C_Price = #{cPrice,jdbcType=REAL},
  C_Weight = #{cWeight,jdbcType=VARCHAR}
where C_ID = #{cId,jdbcType=INTEGER}

mapper.select:
select C_ID, C_Name, C_Count, C_Price, C_Weight
from t_caete
where C_ID = #{cId,jdbcType=INTEGER}
